import React, { useState } from 'react'
import {
  AppProvider, Page,
  Divider, Bleed,
  Card, TextField,
  Button, Box,
  RadioButton,
  Scrollable,
  DataTable, Text,
  LegacyCard,
  BlockStack, InlineGrid, Icon
} from '@shopify/polaris';
import { DeleteIcon } from '@shopify/polaris-icons';
import '@shopify/polaris/build/esm/styles.css';
import { useForm } from 'react-hook-form';
import './App.css'
function App() {
  const [campaign, setCampaign] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [option, setOption] = useState('option1');
  const rows = [
    ['Single', 'None', '1', ''],
    ['Duo', '%discount', '2', '10%'],
  ];
  const buttonText = <Icon source={DeleteIcon} />;
  const handleAction = useCallback(() => {
    setConnected((campaign) => "");
  }, []);
  return (
    <AppProvider i18n={{}}>
      <Page
        backAction={{ content: 'Settings', url: '#' }}
        title="Create volume discount" >
        <InlineGrid columns={{ xs: 1, md: "2fr 1fr" }} gap="400">
          <BlockStack gap="100">
            <LegacyCard title="General" sectioned>
              <TextField
                label="Campaign"
                value={campaign}
                placeholder="Volume discount #2"
                autoComplete="off"
              />
              <TextField
                label="Title"
                value={title}
                placeholder="Buy more and save"
                autoComplete="off"
              />
              <TextField
                label="Description"
                value={description}
                placeholder="Apply for all products store"
                autoComplete="off"
              />
            </LegacyCard>
            <LegacyCard title="Volume discount rule">
              <AccountConnection
                title="Example App"
                action={{
                  content: buttonText,
                  onAction: handleAction,
                }}
              />
            </LegacyCard>
          </BlockStack>
          <BlockStack gap={{ xs: "200", md: "200" }}>
            <LegacyCard title="Preview" sectioned>
              <Box padding="400" >
                <Text fontWeight="bold" alignment="center">Buy more and save</Text>
              </Box>
              <p>Apply for all products in store</p>
              <Scrollable style={{ width: '300px' }}>
                <DataTable
                  columnContentTypes={['text', 'text', 'numeric', 'text']}
                  headings={['Title', 'Discount Type', 'Quantity', 'Amount']}
                  rows={rows}
                />
              </Scrollable>
            </LegacyCard>
          </BlockStack>
        </InlineGrid>
      </Page>
    </AppProvider>

  );
}

export default App;
