import React, { useState, useEffect } from 'react'
import {
  AppProvider, Page,
  Divider, Bleed, TextField,
  Button, Box,
  Scrollable,
  DataTable, Text,
  LegacyCard,
  Select,
  BlockStack, InlineGrid,
  FormLayout, Icon,
  InlineStack
} from '@shopify/polaris';
import { DeleteIcon, PlusCircleIcon, SaveIcon } from '@shopify/polaris-icons';
import '@shopify/polaris/build/esm/styles.css';
import { useForm, Controller, ErrorMessage } from 'react-hook-form';
import * as yup from "yup";
import './App.css'
function App() {
  const [dataModel, setData] = useState(null);

  const SignupSchema = yup.object().shape({
    title: yup.string().required()
  });
  const defaultValues = {
    title: 'Default Title',
    description: 'Default Description',
  };
  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  // option
  const options = [
    { label: '% discount', value: '2' },
    { label: 'Discount / each', value: '1' },
    { label: 'None', value: '0' },
  ];

  var [OptionData, setOptionData] = useState([
    {
      row: 1,
      title: "Single",
      subtitle: "Standard Price",
      lable: '',
      quantity: 1,
      discountType: 0,
      amount: 0
    },
    {
      row: 2,
      title: "Duo",
      subtitle: "Save 10%",
      lable: 'Popular',
      quantity: 2,
      discountType: 2,
      amount: 10
    },
  ]);
  //preview
  const rows = [
    ['Single', 'None', '1', ''],
    ['Duo', '%discount', '2', '10%'],
  ];
  // add option

  const [cards, setCards] = useState([]);
  const BuildCard = (data) => {
    return <BlockStack key={data.row}>
      <Divider borderColor="border" />
      <Bleed marginInline="400">
        <Box
          style={{
            backgroundColor: '#FF4500',
            color: 'white',
            width: '80px',
            borderBottomRightRadius: '5px',
          }}><p class="t-option">OPTION {data.row}</p>
        </Box>
      </Bleed>
      <LegacyCard.Section
        wrap={false} alignment="leading" spacing="loose"
        actions={[{ content: <Button icon={DeleteIcon} onClick={() => handleRemoveCard(data.row)} accessibilityLabel="Delete" /> }]}
      >
        <FormLayout.Group condensed>
          <Controller
            as={
              <TextField
                label="Title"
                onChange={([value]) => value}
                error={errors?.name?.message}
              />
            } onChange={([value]) => {
              return value
            }}
            name="TitleOption"
            control={control}
          />
          <Controller
            as={
              <TextField
                label="Subtitle"
                onChange={([value]) => value}
                error={errors?.name?.message}
              />
            } onChange={([value]) => {
              return value
            }}
            name="SubtitleOption"
            control={control}
          />
          <Controller
            as={
              <TextField
                label="Label (optinal)"
                onChange={([value]) => value}
                error={errors?.name?.message}
              />
            } onChange={([value]) => {
              return value
            }}
            name="LabelOption"
            control={control}
          />
        </FormLayout.Group>
        <br></br>
        <FormLayout.Group condensed>
          <Controller
            as={
              <TextField
                label="Quantity"
                onChange={([value]) => value}
                error={errors?.name?.message}
              />
            } onChange={([value]) => {
              return value
            }}
            name="QuantityOption"
            control={control}
          />
          <Controller
            as={
              <Select
                label="Discount"
                options={options}
                onChange={([value]) => value}
                error={errors?.name?.message}
              />
            } onChange={([value]) => {
              return value
            }}
            name="DiscountOption"
            control={control}
          />
          {data.discountType !== 0 &&
            <Controller
              as={
                <TextField
                  label="Amount"
                  onChange={([value]) => value}
                  error={errors?.name?.message}
                />
              } onChange={([value]) => {
                return value
              }}
              name="AmountOption"
              control={control}
            />}
        </FormLayout.Group>
      </LegacyCard.Section>
    </BlockStack>
  }

  const CardDefaul = () => {
    const newCards = OptionData.map(item => ({
      id: item.row,
      card: BuildCard(item)
    }));
    setCards([...cards, ...newCards]);
  }
  const handleAddCard = () => {
    const maxRow = OptionData.reduce((max, item) => (item.row > max ? item.row : max), OptionData[0].row);
    const maxRowItem = OptionData.filter(item => item.row === maxRow)[0];
    const NewData = {
      row: maxRowItem.row + 1,
      title: '',
      subtitle: '',
      lable: '',
      quantity: maxRowItem.quantity + 1,
      discountType: 0,
      amount: 0
    }
    setOptionData([...OptionData, NewData]);
    const newCard = BuildCard(NewData)
    setCards([...cards, { id: NewData.row, card: newCard }]); // Cập nhật state với card mới
  };
  const handleRemoveCard = (index) => {
    // Loại bỏ card ở chỉ số tương ứng
    const datas = OptionData.filter(item => item.row !== index);
    setOptionData(datas);
    const updatedCards = cards.filter(item => item.id !== index);
    setCards(updatedCards);
    console.log(cards)
  };

  useEffect(() => {
    CardDefaul();
  }, []);

  const onSubmit = dataModel => {
    alert(JSON.stringify(dataModel));
  };
  return (
    <AppProvider i18n={{}}>
      <Page
        backAction={{ content: 'Settings', url: '#' }}
        title="Create volume discount" >
        <InlineGrid columns={{ xs: 1, md: "2fr 1fr" }} gap="400">
          <form onSubmit={handleSubmit(onSubmit)}>
            <BlockStack>
              <LegacyCard title="General" sectioned>
                <Controller
                  as={
                    <TextField
                      label="Campaign"
                      autoComplete="off"
                      placeholder="Volume discount #2"

                      onChange={([value]) => value}
                      error={errors?.campaign?.message}
                    />
                  } onChange={([value]) => {
                    return value
                  }}
                  name="campaign"
                  control={control}
                />
                <br></br>
                <Controller
                  as={
                    <TextField
                      label="Title"
                      autoComplete="off"
                      placeholder="Buy more and save"
                      onChange={([value]) => value}
                      error={errors?.title?.message}
                    />
                  } onChange={([value]) => {
                    return value
                  }}
                  name="title"
                  control={control}
                />
                <br></br>
                <Controller
                  as={
                    <TextField
                      label="Description"
                      autoComplete="off"
                      placeholder="Apply for all products store"
                      onChange={([value]) => value}
                      error={errors?.description?.message}
                    />
                  } onChange={([value]) => {
                    return value
                  }}
                  name="Description"
                  control={control}
                />
              </LegacyCard>
              <LegacyCard title={<div className="mBt">Volume discount rule</div>} sectioned>
                <BlockStack gap="800">
                  {cards.map((cardObj) => (
                    <div key={cardObj.id}>
                      {cardObj.card}
                    </div>
                  ))}
                  <Divider borderColor="border" />
                  <div onClick={() => handleAddCard(1)} className="btn-add" role="button" tabIndex={0}>
                    <InlineStack wrap={false} align="center">
                      <span aria-hidden="true">
                        <Icon source={PlusCircleIcon} />
                      </span>
                      <span> Add Option</span>
                    </InlineStack>
                  </div>
                  <br></br>
                </BlockStack>
              </LegacyCard>
            </BlockStack>
            <BlockStack gap={{ xs: "200", md: "200" }}>
              <LegacyCard title="Preview" sectioned>
                <Box padding="400" >
                  <Text fontWeight="bold" alignment="center">Buy more and save</Text>
                </Box>
                <p>Apply for all products in store</p>
                <Scrollable style={{ width: '300px' }}>
                  <DataTable
                    columnContentTypes={['text', 'text', 'numeric', 'text']}
                    headings={['Title', 'Discount Type', 'Quantity', 'Amount']}
                    rows={rows}
                  />
                </Scrollable>
              </LegacyCard>
            </BlockStack>
            <Button icon={SaveIcon} tone="success" variant="primary" submit>Save</Button>
          </form>
        </InlineGrid>
      </Page>
    </AppProvider>
  );
}

export default App;
