import React, { useState, useCallback } from 'react'
import {
  AppProvider, Page,
  Divider, Bleed,
  Card, TextField,
  Button, Box,
  RadioButton,
  Scrollable,
  DataTable, Text,
  LegacyCard,
  LegacyStack,
  BlockStack, InlineGrid, Icon, AccountConnection
} from '@shopify/polaris';
import { DeleteIcon } from '@shopify/polaris-icons';
import '@shopify/polaris/build/esm/styles.css';
import { useForm } from 'react-hook-form';
import './App.css'
function App() {
  const [campaign, setCampaign] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [option, setOption] = useState('option1');
  const rows = [
    ['Single', 'None', '1', ''],
    ['Duo', '%discount', '2', '10%'],
  ];
  const buttonText = <Button icon={DeleteIcon} accessibilityLabel="Delete" />;
  const handleAction = useCallback(() => {
    setCampaign((campaign) => "");
  }, []);
  return (
    <AppProvider i18n={{}}>
      <Page
        backAction={{ content: 'Settings', url: '#' }}
        title="Create volume discount" >
        <InlineGrid columns={{ xs: 1, md: "2fr 1fr" }} gap="400">
          <BlockStack gap="100">
            <LegacyCard title="General" sectioned>
              <TextField
                label="Campaign"
                value={campaign}
                placeholder="Volume discount #2"
                autoComplete="off"
              />
              <TextField
                label="Title"
                value={title}
                placeholder="Buy more and save"
                autoComplete="off"
              />
              <TextField
                label="Description"
                value={description}
                placeholder="Apply for all products store"
                autoComplete="off"
              />
            </LegacyCard>
            <LegacyCard title={<div className="mBt">Volume discount rule</div>}>
              <Divider borderColor="border-inverse" />
              <Box
                style={{
                  backgroundColor: '#FFA500',
                  color: 'white',
                  width: '80px',
                  borderBottomRightRadius: '5px',
                }}><p padding="4">OPTION</p></Box>
              <LegacyCard.Section
                wrap={false} alignment="leading" spacing="loose"
                actions={[{ content: buttonText }]}
              >
                <LegacyStack wrap={false} alignment="leading" spacing="loose">
                  <LegacyStack.Item fill>
                    <FormLayout>
                      <FormLayout.Group condensed>
                        <Select
                          label="Collection rule type"
                          options={['Product type']}
                          value={selectTypeValue}
                          onChange={handleSelectTypeChange}
                        />
                        <Select
                          label="Collection rule condition"
                          options={['is equal to']}
                          value={selectConditionValue}
                          onChange={handleSelectConditionChange}
                        />
                        <TextField
                          label="Collection rule content"
                          error={isInvalid}
                          id={textFieldID}
                          value={textFieldValue}
                          onChange={handleTextFieldValueChange}
                          autoComplete="off"
                        />
                      </FormLayout.Group>
                    </FormLayout>
                  </LegacyStack.Item>
                </LegacyStack>
              </LegacyCard.Section>
            </LegacyCard>
          </BlockStack>
          <BlockStack gap={{ xs: "200", md: "200" }}>
            <LegacyCard title="Preview" sectioned>
              <Box padding="400" >
                <Text fontWeight="bold" alignment="center">Buy more and save</Text>
              </Box>
              <p>Apply for all products in store</p>
              <Scrollable style={{ width: '300px' }}>
                <DataTable
                  columnContentTypes={['text', 'text', 'numeric', 'text']}
                  headings={['Title', 'Discount Type', 'Quantity', 'Amount']}
                  rows={rows}
                />
              </Scrollable>
            </LegacyCard>
          </BlockStack>
        </InlineGrid>
      </Page>
    </AppProvider>
  );
}

export default App;
