import React, { useState } from 'react'
import { AppProvider, Page,Grid, Card, TextField, Button, Layout, RadioButton, LegacyCard } from '@shopify/polaris';
import '@shopify/polaris/build/esm/styles.css';
import { useForm } from 'react-hook-form';
import './App.css'
function App() {
  const [campaign, setCampaign] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [option, setOption] = useState('option1');
  return (
    <AppProvider i18n={{}}>
      <Page
        backAction={{ content: 'Settings', url: '#' }}
        title="Create volume discount" >
       <Layout>
        <Layout.Section>
        <Grid>
        <Grid.Cell columnSpan={{xs: 7, sm: 3, md: 3, lg: 5, xl: 5}}>
          <LegacyCard title="Sales" sectioned>
            <p>View a summary of your online store’s sales.</p>
          </LegacyCard>
        </Grid.Cell>
        <Grid.Cell columnSpan={{xs: 7, sm: 3, md: 3, lg: 5, xl: 5}}>
          <LegacyCard title="Orders" sectioned>
            <p>View a summary of your online store’s orders.</p>
          </LegacyCard>
        </Grid.Cell>
      </Grid>
        </Layout.Section>
        <Layout.Section variant="fullWidth">
          <LegacyCard title="Tags" sectioned>
            <p>Add tags to your order.</p>
          </LegacyCard>
        </Layout.Section>
      </Layout>
      </Page>
    </AppProvider>

  );
}

export default App;
