import React, { useState, useCallback, useEffect } from 'react'
import {
  AppProvider, Page,
  Divider, Bleed,
  Card, TextField,
  Button, Box,
  RadioButton,
  Scrollable,
  DataTable, Text,
  LegacyCard,
  LegacyStack, Select,
  BlockStack, InlineGrid, FormLayout, Icon, InlineStack
} from '@shopify/polaris';
import { DeleteIcon, PlusCircleIcon } from '@shopify/polaris-icons';
import '@shopify/polaris/build/esm/styles.css';
import { useForm } from 'react-hook-form';
import './App.css'
function App() {
  // general
  const [campaign, setCampaign] = useState('');
  const handleCampaignChange = useCallback(
    (newValue) => setValue(newValue),
    [],
  );
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const handleAction = useCallback(() => {
    setCampaign((campaign) => "");
  }, []);
  // option
  const [option, setOption] = useState('option1');
  const [selected1, setSelected] = useState('0');
  const [selected2, setSelected2] = useState('2');
  const options = [
    { label: '% discount', value: '2' },
    { label: 'Discount / each', value: '1' },
    { label: 'None', value: '0' },
  ];

  //preview
  const rows = [
    ['Single', 'None', '1', ''],
    ['Duo', '%discount', '2', '10%'],
  ];
  // add option

  const [cards, setCards] = useState([]);
  const BuildCard = (index) => {
    return <BlockStack key={index}>
      <Divider borderColor="border" />
      <Bleed marginInline="400">
        <Box
          style={{
            backgroundColor: '#FF4500',
            color: 'white',
            width: '80px',
            borderBottomRightRadius: '5px',
          }}><p class="t-option">OPTION {index}</p>
        </Box>
      </Bleed>
      <LegacyCard.Section
        wrap={false} alignment="leading" spacing="loose"
        actions={[{ content: <Button icon={DeleteIcon} onClick={() => handleRemoveCard(index)} accessibilityLabel="Delete" /> }]}
      >
        <FormLayout.Group condensed>
          <TextField
            label="Title"
            value={title}
          />
          <TextField
            label="Subtitle"
            value={subtitle}
          />
          <TextField
            label="Label (optinal)"
            autoComplete="off"
          />
        </FormLayout.Group>
        <br></br>
        <FormLayout.Group condensed>
          <TextField
            label="Quantity"
          />
          <Select
            label="Discount"
            options={options}
            value={selected2}
          />
          <TextField
            label="Amount"
            autoComplete="off"
          />
        </FormLayout.Group>
      </LegacyCard.Section>
    </BlockStack>
  }
  const handleAddCard = (num) => {
    // Thêm một card mới vào danh sách
    const defaultCards = [];
    let newCardIndex = cards.length; 
    for (let i = 0; i < num; i++) {
      newCardIndex += 1;
      defaultCards.push(BuildCard(newCardIndex))
    }
    setCards([...cards, ...defaultCards]); // Cập nhật state với card mới
  };
  const handleRemoveCard = (index) => {
    // Loại bỏ card ở chỉ số tương ứng
    const updatedCards = cards.filter((_, cardIndex) => cardIndex !== index);
    setCards(updatedCards);
  };

  useEffect(() => {
    handleAddCard(2);
  }, []);
  return (
    <AppProvider i18n={{}}>
      <Page
        backAction={{ content: 'Settings', url: '#' }}
        title="Create volume discount" >
        <InlineGrid columns={{ xs: 1, md: "2fr 1fr" }} gap="400">
          <BlockStack>
            <LegacyCard title="General" sectioned>
              <TextField
                label="Campaign"
                value={campaign}
                onChange={handleCampaignChange}
                placeholder="Volume discount #2"
                autoComplete="off"
              />
              <br></br>
              <TextField
                label="Title"
                value={title}
                placeholder="Buy more and save"
                autoComplete="off"
              />
              <br></br>
              <TextField
                label="Description"
                value={description}
                placeholder="Apply for all products store"
                autoComplete="off"
              />
            </LegacyCard>
            <LegacyCard title={<div className="mBt">Volume discount rule</div>} sectioned>
              <BlockStack gap="800">
                {cards}
                <Divider borderColor="border" />
                <div  onClick={() => handleAddCard(1)} className="btn-add" role="button" tabIndex={0}>
                  <InlineStack wrap={false} align="center">
                    <span aria-hidden="true">
                      <Icon source={PlusCircleIcon} />
                    </span>
                    <span> Add Option</span>
                  </InlineStack>
                </div>
                <br></br>
              </BlockStack>
            </LegacyCard>
          </BlockStack>
          <BlockStack gap={{ xs: "200", md: "200" }}>
            <LegacyCard title="Preview" sectioned>
              <Box padding="400" >
                <Text fontWeight="bold" alignment="center">Buy more and save</Text>
              </Box>
              <p>Apply for all products in store</p>
              <Scrollable style={{ width: '300px' }}>
                <DataTable
                  columnContentTypes={['text', 'text', 'numeric', 'text']}
                  headings={['Title', 'Discount Type', 'Quantity', 'Amount']}
                  rows={rows}
                />
              </Scrollable>
            </LegacyCard>
          </BlockStack>
        </InlineGrid>
      </Page>
    </AppProvider>
  );
}

export default App;
